import { useEffect, useState } from 'react';
import "./styles.css";
import axios from 'axios';
import { AuthenticationClient } from 'react';


export default function App() {
    const CLIENT_ID = "8921e1b7621a44c7a1bed3041fe41aa2";
    const REDIRECT_URI = "http://localhost:3000";
    const AUTH_ENDPOINT = "https://accounts.spotify.com/authorize";
    const RESPONSE_TYPE = "token";

    const [token, setToken] = useState("");
    const [searchKey, setSearchKey] = useState("");
    const [artists, setArtists] = useState([]);


    const logout = () => {
        window.localStorage.clear();
        setToken("");
    }

    const renderArtists = () => {
        return artists.map(artist => (
            <div className="artists"key={artist.id}>
                {artist.images.length ? <img src={artist.images[0].url} alt=""/> : <div>No Image</div>}
                <p>
                    {artist.name}
                </p>
            </div>
        ))
    }

    const getPlaylist = () => {

    }

    const searchArtists = async (e) => {
        const auth = "Bearer " + token;
        console.log(auth);
        e.preventDefault();
        const {data} = await axios.get("https://api.spotify.com/v1/search", {
            headers: {
                Authorization: auth
            },
            params: {
                q: searchKey,
                type: "artist"
            }
        })
        setArtists(data.artists.items);
    }

    const getTrack = async (e) => {
        const auth = "Bearer " + token;
        console.log(auth);
        e.preventDefault();
        const url = "https://api.spotify.com/v1/audio-features/" + searchKey;
        const {data} = await axios.get(url, {
            headers: {
                Authorization: auth
            }
        })
        console.log(data);
    }
    useEffect(() => {
        const hash = window.location.hash;
        let token = window.localStorage.getItem("token");
        setToken(token);

        if (!token && hash) {
            token = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1];
            window.location.hash = "";
            window.localStorage.setItem("token", token);
        }
    }, [token]);

    const renderLoginLogout = () => {
        if (!token) {
            return (
                <>
                <p>
                <a href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}`}>Login to Spotify</a>
                </p>
                </>
            )
        }
        else {
            return (
                <>
                <button onClick={logout}>Logout</button>
                </>
            )
        }
    }

    return (
        <>
        <div className='inputBox'>
            <form onSubmit={searchArtists}>
                <input type="text" name="Enter artist name..." onChange={e => setSearchKey(e.target.value)}/>
                <button type={"submit"}>Search</button>
            </form>
            <form onSubmit={getTrack}>
                <input type="text" name="Enter playlist link..." onChange={e => setSearchKey(e.target.value)}/>
                <button type={"submit"}>Search</button>
            </form>
        </div>
        {renderLoginLogout()}
        {renderArtists()}
        </>
    );
}
